package codegen

const testBaseTemplate = `
{{- define "TestBase" -}}
// WARNING: This file is machine generated by fidlgen.

#pragma once

{{ $root := . -}}
{{ range .Headers -}}
#include <{{ . }}/{{ $root.IncludeStem }}.h>
{{ end -}}

#include <{{ .PrimaryHeader }}>

{{- range .Decls }}
{{- if Eq .Kind Kinds.Protocol }}{{ template "ProtocolTestBase" . }}{{- end }}
{{- end -}}

{{ EndOfFile }}
{{ end }}
`
